import tkinter.messagebox
import turtle

turtle.title("#By Fawwaz")
turtle.bgcolor("green")
p1 = turtle.Turtle()
p1.pencolor("Navy")
p1.pensize(10)
for i in range(1, 7):
    p1.xcor()
    p1.setx(x=True)
    p1.resizemode()
    p1.speed(10)
    p1.distance(p1)
    p1.forward(100)
    p1.clear()
    p1.left(90)
    p1.forward(100)

















