import turtle

while True:
  player1 = turtle.Turtle()
  player1.begin_fill()
  player1.forward(100)
  player1.left(90)
  player1.forward(100)
  player1.left(90)
  player1.forward(100)
  player1.left(90)
  player1.forward(100)
  player1.left(90)
  player1.forward(100)
  player1.left(90)
  player1.forward(100)
  player1.left(90)
  player1.end_fill()
  player1.forward(700)

  player2 = turtle.Turtle()
  player2.back(100)
  player2.begin_fill()
  player2.forward(100)
  player2.left(90)
  player2.forward(100)
  player2.left(90)
  player2.forward(100)
  player2.left(90)
  player2.forward(100)
  player2.left(90)
  player2.forward(100)
  player2.left(90)
  player2.forward(100)
  player2.left(90)
  player2.end_fill()
  player2.back(700)

  player3 = turtle.Turtle()
  player3.back(700)

  player4 = turtle.Turtle()
  player4.forward(700)